import React, { Component } from 'react';
import {Tabs, Tab, Grid, Cell, Card, CardTitle, CardText, CardActions, Button, CardMenu, IconButton} from 'react-mdl';

class Projects extends Component {
    constructor(props) {
        super(props);
        this.state = { activeTab: 0 };
    }

    toggleCategories() {

        if (this.state.activeTab === 0) {
            return (
                <Card shadow={5} style={{minWidth: '450', margin: 'auto'}}>
                    <CardTitle style={{color: 'black', height: '176px', background:
                            'url(https://upload.wikimedia.org/wikipedia/commons/thumb/a/a7/React-icon.svg/2000px-React-icon.svg.png) center /cover'}}> React Project #1</CardTitle>
                <CardText>
                    This is the first of many React projects
                </CardText>
                    <CardActions border>
                        <Button colored>GitHub</Button>
                        <Button colored>CodePen</Button>
                        <Button colored>LiveDemo</Button>
                    </CardActions>
                    <CardMenu style={{color: 'black'}}>
                        <IconButton name={share}/>
                    </CardMenu>
                </Card>
            )
        }
        else if (this.state.activeTab === 1) {
            return (
                <div><h1>This is Java</h1></div>
            )
        }
        else if (this.state.activeTab === 2) {
            return (
                <div><h1>This is C++</h1></div>
            )
        }
        else if (this.state.activeTab === 3) {
            return (
                <div><h1>This is Python</h1></div>
            )
        }
    }

    render(){
        return(
            <div className="category-tabs">
                <Tabs activeTab={this.state.activeTab} onChange={(tabID) => this.setState({ activeTab: tabID})} ripple>
                    <Tab>React</Tab>
                    <Tab>Java</Tab>
                    <Tab>C++</Tab>
                    <Tab>Python</Tab>
                </Tabs>

                <section className="projects-grid">
                    <Grid className="projects-grid">
                        <Cell col={12}>
                            <div className={"content"}> {this.toggleCategories()}</div>
                        </Cell>
                    </Grid>

                </section>
            </div>
        )
    }
}

export default Projects;
